move.effects:
var attack1 = performer.getAttackLevel();
var specialAttack1 = performer.getSpecialAttackLevel();
var attack2 = defender.getAttackLevel();
var specialAttack2 = defender.getSpecialAttackLevel();

performer.setAttackLevel(attack2);
performer.setSpecialAttackLevel(specialAttack2);
defender.setAttackLevel(attack1);
defender.setSpecialAttackLevel(specialAttack1);
